import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/button";
import "./ToolForm.css";
import Checklist from "./Checklist";
import useState from "react/useState";

const ToolForm = () => {
  const [color, changeColor] = useState({ text: "inherit" });

  const onSubmit = () => {
    const colorNumber = Math.round(Math.random() * 1);
    if (colorNumber) {
      changeColor("red");
    } else {
      changeColor("inherit");
    }
  };

  return (
    <div>
      <div
        style={{
          marginTop: "1%",
          width: "45%",
          marginLeft: "27.5%",
          marginRight: "27.5%",
          border: "1px solid lightGray",
          boxShadow: "0px 3px 10px 0px gray",
          padding: "1%",
          background: "lightgray"
        }}
      >
        <Checklist />
        {/* <h2 className="tool">TITLE</h2>
        <Form.Control type="text" placeholder="placeholder" />
        <br /> */}
        <h2 className="tool">ARTIST</h2>
        <Form.Control
          type="text"
          placeholder="artist"
          style={{ border: `${color}` }}
        />
        <br />
        <h2 className="tool">PUBLISHER</h2>
        <Form.Control
          type="text"
          placeholder="publisher"
          style={{ border: `${color}` }}
        />
        <br />
        <h2 className="tool">RELEASE DATE</h2>
        <Form.Control
          type="text"
          placeholder="release"
          style={{ border: `${color}` }}
        />
        <br />
        <h2 className="tool">ISWC</h2>
        <Form.Control
          type="text"
          placeholder="iswc"
          style={{ border: `${color}` }}
        />
        <br />
        <Button variant="secondary" onChange={onSubmit}>
          Submit
        </Button>
      </div>
    </div>
  );
};

export default ToolForm;
